'create or replace PACKAGE BODY habeeb_compute_gpa AS',
'',
'  FUNCTION calculate_gpa(p_student_id NUMBER, p_semester VARCHAR2) RETURN NUMBER IS',
'    v_gpa NUMBER(4,2);',
'  BEGIN',
'    SELECT ROUND(SUM(s.grade_point * c.unit) / SUM(c.unit), 2)',
'    INTO v_gpa',
'    FROM habeeb_scores s',
'    JOIN habeeb_course_registrations r ON s.registration_id = r.id',
'    JOIN habeeb_courses c ON r.course_id = c.id',
'    WHERE r.student_id = p_student_id',
'      AND c.semester = p_semester;',
'',
'    RETURN v_gpa;',
'  EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
'      RETURN NULL;',
'  END calculate_gpa;',
'',
'  FUNCTION calculate_cgpa(p_student_id NUMBER) RETURN NUMBER IS',
'    v_cgpa NUMBER(4,2);',
'  BEGIN',
'    SELECT ROUND(SUM(s.grade_point * c.unit) / SUM(c.unit), 2)',
'    INTO v_cgpa',
'    FROM habeeb_scores s',
'    JOIN habeeb_course_registrations r ON s.registration_id = r.id',
'    JOIN habeeb_courses c ON r.course_id = c.id',
'    WHERE r.student_id = p_student_id;',
'',
'    RETURN v_cgpa;',
'  EXCEPTION',
'    WHEN NO_DATA_FOUND THEN',
'      RETURN NULL;',
'  END calculate_cgpa;',
'',
'  PROCEDURE update_gpa_summary(p_student_id NUMBER, p_semester VARCHAR2) IS',
'    v_gpa NUMBER(4,2);',
'    v_cgpa NUMBER(4,2);',
'  BEGIN',
'    v_gpa := calculate_gpa(p_student_id, p_semester);',
'    v_cgpa := calculate_cgpa(p_student_id);',
'',
'    MERGE INTO habeeb_gpa_summary g',
'    USING dual',
'    ON (g.student_id = p_student_id AND g.semester = p_semester)',
'    WHEN MATCHED THEN',
'      UPDATE SET g.gpa = v_gpa, g.cgpa = v_cgpa',
'    WHEN NOT MATCHED THEN',
'      INSERT (student_id, semester, gpa, cgpa)',
'      VALUES (p_student_id, p_semester, v_gpa, v_cgpa);',
'  END update_gpa_summary;',
'',
'END habeeb_compute_gpa;',
