'DECLARE',
'    p_student_id NUMBER;',
'    p_course_id NUMBER;',
'    p_semester VARCHAR2(20);',
'    v_payment_count NUMBER;',
'    v_registration_count NUMBER; -- Add this line',
'BEGIN',
'    -- Get student ID from matric number',
'    SELECT id',
'    INTO p_student_id',
'    FROM habeeb_students',
'    WHERE lower(matric_no) = lower(:APP_USER);',
'',
'    -- Get course details',
'    SELECT id,',
'           semester',
'    INTO p_course_id,',
'         p_semester',
'    FROM habeeb_courses',
'    WHERE id = :P3_ID;',
'',
'    -- Check if tuition has been paid for the semester',
'    SELECT count(*)',
'    INTO v_payment_count',
'    FROM habeeb_studmngt_payments',
'    WHERE student_id = p_student_id',
'      AND semester = p_semester',
'      AND payment_type = ''Tuition'';',
'',
'    IF v_payment_count = 0 THEN',
'        raise_application_error(',
'            -20007,',
'            ''Error: You must pay the ''',
'            || p_semester',
'            || '' semester tuition fees before registering for this course.''',
'        );',
'    END IF;',
'',
'    -- Check if the course has already been registered by the student',
'    SELECT count(*)',
'    INTO v_registration_count',
'    FROM habeeb_course_registrations',
'    WHERE student_id = p_student_id',
'      AND course_id = p_course_id;',
'',
'    IF v_registration_count > 0 THEN',
'        raise_application_error(',
'            -20008, -- Use a new error number',
'            ''Error: You have already registered for this course.''',
'        );',
'    END IF;',
'',
'    -- Insert new course registration',
'    INSERT INTO habeeb_course_registrations (',
'        student_id,',
'        course_id',
'    ) VALUES (',
'        p_student_id,',
'        p_course_id',
'    );',
'',
'EXCEPTION',
'    WHEN no_data_found THEN',
'        raise_application_error(',
'            -20005,',
'            ''Error: Invalid student or course selected.''',
'        );',
'    WHEN OTHERS THEN',
'        -- Catch the unique constraint violation specifically',
'        IF sqlcode = -1 THEN',
'            raise_application_error(',
'                -20008,',
'                ''Error: You have already registered for this course.''',
'            );',
'        ELSE',
'            raise_application_error(',
'                -20006,',
'                sqlerrm',
'            );',
'        END IF;',
'END;'))
