'	"ACADEMIC_SESSION" VARCHAR2(5) DEFAULT ''24/25'' NOT NULL ENABLE, ',
'	 PRIMARY KEY ("ID")',
'  USING INDEX  ENABLE, ',
'	 CONSTRAINT "HABEEB_UNIQUE_REGISTRATION" UNIQUE ("STUDENT_ID", "COURSE_ID")',
'  USING INDEX  ENABLE',
'   ) ;',
'',
'  CREATE TABLE "HABEEB_GPA_SUMMARY" ',
'   (	"ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, ',
'	"STUDENT_ID" NUMBER NOT NULL ENABLE, ',
'	"GPA" NUMBER(4,2), ',
'	"CGPA" NUMBER(4,2), ',
'	"SEMESTER" VARCHAR2(50), ',
'	 PRIMARY KEY ("ID")',
'  USING INDEX  ENABLE',
'   ) ;',
'',
'  CREATE TABLE "HABEEB_PAYMENT_REQUIREMENTS" ',
'   (	"REQUIREMENT_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, ',
'	"PAYMENT_TYPE" VARCHAR2(30) NOT NULL ENABLE, ',
'	"AMOUNT_EXPECTED" NUMBER(10,2) NOT NULL ENABLE, ',
'	"SEMESTER" VARCHAR2(20) NOT NULL ENABLE, ',
'	 CONSTRAINT "CHK_PAYMENT_TYPE_REQ" CHECK (payment_type IN (''Tuition'', ''Exam'', ''Registration'')) ENABLE, ',
'	 CONSTRAINT "CHK_SEMESTER_REQ" CHECK (semester IN (''FIRST'', ''SECOND'')) ENABLE, ',
'	 PRIMARY KEY ("REQUIREMENT_ID")',
'  USING INDEX  ENABLE, ',
'	 CONSTRAINT "UNIQUE_PAYMENT_TYPE_PER_SEMESTER" UNIQUE ("PAYMENT_TYPE", "SEMESTER")',
'  USING INDEX  ENABLE',
'   ) ;',
'',
'  CREATE TABLE "HABEEB_SCORES" ',
'   (	"ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, ',
'	"REGISTRATION_ID" NUMBER NOT NULL ENABLE, ',
'	"MIDTERM" NUMBER(3,0) NOT NULL ENABLE, ',
'	"FINAL" NUMBER(3,0) NOT NULL ENABLE, ',
'	"TOTAL" NUMBER(3,0), ',
'	"GRADE" CHAR(1), ',
'	"GRADE_POINT" NUMBER(3,2), ',
'	 CHECK (midterm BETWEEN 0 AND 30) ENABLE, ',
'	 CHECK (final BETWEEN 0 AND 70) ENABLE, ',
'	 PRIMARY KEY ("ID")',
'  USING INDEX  ENABLE, ',
'	 CONSTRAINT "UNIQUE_REGISTRATION_ID" UNIQUE ("REGISTRATION_ID")',
'  USING INDEX  ENABLE',
'   ) ;',
'',
'  CREATE TABLE "HABEEB_SEMESTERS" ',
'   (	"ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, ',
'	"NAME" VARCHAR2(50) NOT NULL ENABLE, ',
'	"YEAR_SESSION" VARCHAR2(20) NOT NULL ENABLE, ',
'	 PRIMARY KEY ("ID")',
'  USING INDEX  ENABLE',
'   ) ;',
'',
'  CREATE TABLE "HABEEB_STUDENTMNGT_DEPARTMENTS" ',
'   (	"DEPARTMENT_ID" NUMBER GENERATED ALWAYS AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, ',
'	"DEPARTMENT_NAME" VARCHAR2(100) NOT NULL ENABLE, ',
'	 PRIMARY KEY ("DEPARTMENT_ID")',
'  USING INDEX  ENABLE',
'   ) ;',
'',
'  CREATE TABLE "HABEEB_STUDENTS" ',
'   (	"ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, ',
'	"NAME" VARCHAR2(100) NOT NULL ENABLE, ',
'	"MATRIC_NO" VARCHAR2(20) NOT NULL ENABLE, ',
'	"DEPARTMENT" VARCHAR2(100), ',
'	"PASSWORD" VARCHAR2(255), ',
'	"DEPARTMENT_ID" NUMBER, ',
'	 PRIMARY KEY ("ID")',
'  USING INDEX  ENABLE, ',
'	 UNIQUE ("MATRIC_NO")',
'  USING INDEX  ENABLE',
'   ) ;',
'',
'  CREATE TABLE "HABEEB_STUDMNGT_PAYMENTS" ',
'   (	"PAYMENT_ID" NUMBER GENERATED BY DEFAULT AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, ',
'	"STUDENT_ID" NUMBER NOT NULL ENABLE, ',
'	"REQUIREMENT_ID" NUMBER NOT NULL ENABLE, ',
'	"PAYMENT_TYPE" VARCHAR2(30) NOT NULL ENABLE, ',
'	"AMOUNT_PAID" NUMBER(10,2) NOT NULL ENABLE, ',
