'      INTO l_student_id, l_matric_no',
'      FROM habeeb_students',
'     WHERE LOWER(matric_no) = LOWER(l_app_user);',
'',
'    l_amount := l_amount * 100;',
'    l_email := l_matric_no || ''@school.edu'';',
'',
'    apex_json.initialize_clob_output;',
'    apex_json.open_object;',
