'    apex_json.write(''amount'', l_amount);',
'    apex_json.write(''reference'', l_reference);',
'    apex_json.open_object(''metadata'');',
'    apex_json.write(''student_id'', l_student_id);',
'    apex_json.write(''requirement_id'', p_requirement_id);',
'    apex_json.write(''payment_type'', p_payment_type);',
'    apex_json.write(''semester'', p_semester);',
'    apex_json.close_object;',
'    apex_json.close_object;',
'    l_json_payload := apex_json.get_clob_output;',
