'      apex_error.add_error(',
'        p_message          => ''Failed to retrieve authorization URL from payment gateway.'',',
'        p_display_location => apex_error.c_inline_in_notification',
'      );',
'    END IF;',
'',
'  EXCEPTION',
'    WHEN no_data_found THEN',
'      apex_error.add_error(',
'        p_message          => ''Payment requirement not found. Please contact an administrator.'',',
'        p_display_location => apex_error.c_inline_in_notification',
